import React from "react"
import PropTypes from "prop-types"
import "./Guidelines.sass"
import {Container, Row, Col} from 'react-bootstrap'

export const Guidelines = ({ heading, description, guidelines, image}) => {
  return (
    <div className="guide-lines-component" style={{ backgroundImage: 'url(./images/dots.png)' }}>
      <Container>
        <Row>
          <Col>
            <h1 className="guidelines-h1">{heading}</h1>
            <h2 className="guidelines-h2">{description}</h2>
            <ol className="guidelines-ol">
              {!guidelines || guidelines.map((guideline, i) => (
                <li className="guidelines-li" key={i}>{guideline}</li>
              ))}
            </ol>
          </Col>
          {image ? <Col>
            <img alt="logo" src={image} style={{maxWidth: "400px"}} />
          </Col> : null}
        </Row>
      </Container>
    </div>
  )
}

Guidelines.propTypes = {
  heading: PropTypes.string,
  description: PropTypes.string,
  guidelines: PropTypes.array
}
