import React from "react"
import PropTypes from "prop-types"
import "./HeaderComponent.sass"
import {Container, Row, Col} from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

export const HeaderComponent = ({mainText, subText, icon, buttonText, buttonLink, image, small, backgroundColor}) => {
  return (
    <div style={{ backgroundImage: `url(${'../ContactComponent/images/dots.png'})` }} className="header-component" >
      <Container>
        {small ? (
          <Row>
            <Col className="small-header-col">    
              <h1>
                {!icon || <FontAwesomeIcon icon={icon} className="icon"/>}{" "}
                {mainText}
              </h1>
            </Col>
          </Row>
        ) : (
          <Row>
            <Col md={6} className="left-col">
              <div className="header-content-section">     
                { mainText ? <h1>
                  <span className='colored'>{mainText.split(' ')[0]}</span>
                  <span>{mainText.split(' ').map((t, i) => i !== 0 ? ' ' + t : null)}</span>
                </h1> : null }
                <p className="sub-text">{subText}</p>
                <a href={buttonLink} className="btn">{buttonText}</a>
              </div>
            </Col>
            { image ? <Col md={6} className="right-col">
              <img className= "main-image" alt="Header" src={image} />
            </Col> : null }
          </Row>
        )}
      </Container>
    </div>
  )
}


HeaderComponent.propTypes = {
  mainText: PropTypes.string,
  subText: PropTypes.string,
  buttonText: PropTypes.string,
  buttonLink: PropTypes.string,
  image: PropTypes.string,
  small: PropTypes.bool,
  icon: PropTypes.string,
  backgroundColor: PropTypes.string
}
