import React from "react"
import PropTypes from "prop-types"
import "./PageNotFound.sass"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faExclamation } from '@fortawesome/free-solid-svg-icons';

export const PageNotFound = ({ image, mainText, subText, header }) => {
  return (
    <div className="page-not-found-component">
    {header ? <div className="header-component">
        <h3><FontAwesomeIcon className="icon" icon={faExclamation} /> {header}</h3>
    </div> : null}
      <div className="inner-container">
        <img src={image} alt='404'/>
          <h1>{mainText}</h1>
          <p>
            {subText ? subText + '|' : null} Return to <a href='/'>Home</a> page
          </p>
      </div>
    </div>
  )
}

PageNotFound.propTypes = {
  image: PropTypes.string,
  mainText: PropTypes.string,
  subText: PropTypes.string,
}
